/*Table structure for table `checkflow` */

DROP TABLE IF EXISTS `checkflow`;

CREATE TABLE `checkflow` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `check_planning_id` int(11) NOT NULL COMMENT '盘点计划ID',
  `type` tinyint(2) NOT NULL COMMENT '盘点类型',
  `name` varchar(40) NOT NULL COMMENT '表单名',
  `sn` varchar(40) NOT NULL COMMENT '表单号',
  `total_buying_amount` decimal(10,2) NOT NULL COMMENT '盘点前总购买金额',
  `check_buying_amount` decimal(10,2) NOT NULL COMMENT '审核后总购买金额',
  `status` tinyint(2) NOT NULL COMMENT '状态',
  `config_id` int(11) NOT NULL COMMENT '流程ID',
  `create_admin_id` int(11) NOT NULL COMMENT '制表人ID',
  `create_time` datetime NOT NULL COMMENT '制表时间',
  `verify_admin_id` int(11) DEFAULT NULL COMMENT '审核人ID',
  `verify_time` datetime DEFAULT NULL COMMENT '审核时间',
  `approval_admin_id` int(11) DEFAULT NULL COMMENT '批准人ID',
  `approval_time` datetime DEFAULT NULL COMMENT '批准时间',
  `operation_admin_id` int(11) DEFAULT NULL COMMENT '执行人ID',
  `operation_time` datetime DEFAULT NULL COMMENT '执行时间',
  `failCause` varchar(255) DEFAULT NULL COMMENT '驳回理由',
  `timing_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '定时操作类型',
  `department_id` int(11) DEFAULT NULL COMMENT '盘点部门ID',
  `warehouse_id` int(11) DEFAULT NULL COMMENT '盘点仓库ID',
  `is_buckle` tinyint(2) NOT NULL DEFAULT '0' COMMENT '是否扣仓【0：否 1：是】',
  `end_time` date NOT NULL COMMENT '盘点结束时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='盘点流程表';

